param([Parameter(Mandatory=$true)]	[string]$RSG,	$TagsHash = @())#@{environment="";buildBy="";buildDate="";Application="";Department="";'RaxAutomation|Exclude'=""}Get-AzureRmResourceGroup $RSG | Set-AzureRmResourceGroup -Tag $TagsHash$resourceGroupName= $RSG$tags_to_apply=(Get-AzureRmResourceGroup -Name $resourceGroupName).tagsGet-AzureRmResource -ResourceGroupName $resourceGroupName | foreach {    Set-AzureRmResource -ResourceId $_.resourceid -Tag $tags_to_apply -Force}
